import React, { useEffect } from 'react';
import './Verify.css';
import { useNavigate, useSearchParams } from 'react-router-dom';
import { useContext } from 'react';
import { StoreContext } from '../../context/StoreContext';
import axios from 'axios';

const Verify = () => {
    const [searchParams] = useSearchParams();
    const success = searchParams.get("success");
    const orderId = searchParams.get("orderId");
    const { url } = useContext(StoreContext);
    const navigate = useNavigate();

    const verifyPayment = async () => {
        try {
            const response = await axios.post(url + "/api/order/verify", { success, orderId });
            if (response.data.success) {
                navigate("/myorders"); // Ensure this route exists
            } else {
                console.error('Verification failed:', response.data.message);
                navigate("/"); // Redirect to home or an error page
            }
        } catch (error) {
            console.error('Error during verification:', error.response?.data || error.message);
            navigate("/"); // Redirect to home or an error page
        }
    };

    useEffect(() => {
        verifyPayment();
    }, []); // Dependency array is empty to run this effect only once after the initial render

    return (
        <div className='verify'>
            <div className="spinner">
                {/* Optional: Add a spinner component or CSS animation here */}
            </div>
            <div>
                <h1>Payment is in process... Don't go back.</h1>
            </div>
        </div>
    );
};

export default Verify;
