import React, { useState, useEffect } from 'react';
import './Navbar.css';
import { assets } from '../../assets/assets';
import { Link } from 'react-router-dom';

const Navbar = ({ setShowLogin }) => {
  const [activeSection, setActiveSection] = useState('home');

  useEffect(() => {
    const handleScroll = () => {
      const sections = document.querySelectorAll('section'); // Ensure sections are identified
      const scrollPosition = window.scrollY + window.innerHeight / 2;

      sections.forEach((section) => {
        const sectionId = section.getAttribute('id');
        if (section.offsetTop <= scrollPosition && (section.offsetTop + section.offsetHeight) > scrollPosition) {
          setActiveSection(sectionId);
        }
      });
    };

    window.addEventListener('scroll', handleScroll);

    // Clean up the event listener on component unmount
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className='navbar'>
      <Link to={'/'}><img src={assets.Ff} alt="Logo" className='logo' /></Link>
      <ul className='navbar-menu'>
        <li>
          <Link 
            to={'/'} 
            className={activeSection === 'home' ? 'active' : ''}
          >
            Home
          </Link>
        </li>
        <li>
          <a 
            href='#explore-menu' 
            className={activeSection === 'explore-menu' ? 'active' : ''}
          >
            Menu
          </a>
        </li>
        <li>
          <a 
            href='#app-download' 
            className={activeSection === 'app-download' ? 'active' : ''}
          >
            Mobile App
          </a>
        </li>
        <li>
          <a 
            href='#footer' 
            className={activeSection === 'footer' ? 'active' : ''}
          >
            Contact Us
          </a>
        </li>
      </ul>
      <div className="navbar-right">
        <img src={assets.search_icon} alt="Search Icon" className="navbar-search-icon" id='search_icon'/>
        <div className="navbar-cart-icon">
          <Link to={'/cart'}>
            <img src={assets.basket_icon} alt="Basket Icon" />
          </Link>
          <div className="dot"></div>
        </div>
        <button className='navbar-button' onClick={() => setShowLogin(true)}>Sign in</button>
      </div>
    </div>
  );
};

export default Navbar;
